import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import api, { bookService } from '../services/api';

const AdminDashboard = () => {
  // User management actions
  const deactivateUser = async (userId) => {
    if (window.confirm('Are you sure you want to deactivate this user?')) {
      try {
        await api.put(`/users/deactivate/${userId}`);
        fetchUsers();
      } catch (error) {
        alert('Failed to deactivate user');
      }
    }
  };

  const reactivateUser = async (userId) => {
    if (window.confirm('Are you sure you want to reactivate this user?')) {
      try {
        await api.put(`/users/reactivate/${userId}`);
        fetchUsers();
      } catch (error) {
        alert('Failed to reactivate user');
      }
    }
  };

  const deleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await api.delete(`/users/delete/${userId}`);
        fetchUsers();
      } catch (error) {
        alert('Failed to delete user');
      }
    }
  };
  const { isAdmin } = useAuth();
  const navigate = useNavigate();
  const [books, setBooks] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingBook, setEditingBook] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showReports, setShowReports] = useState(false);
  const [salesData, setSalesData] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    pages: '',
    genre: '',
    quantity: '',
    price: '',
    image: null
  });

    // User management state
    const [showUsers, setShowUsers] = useState(false);
    const [users, setUsers] = useState([]);
    const [usersLoading, setUsersLoading] = useState(false);
    const [usersError, setUsersError] = useState('');

  return (
    <div className="container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <h2>üìö Admin Dashboard - Snuggle Read</h2>
        <div style={{ display: 'flex', gap: '1rem' }}>
          <button className="btn btn-outline" onClick={generateSalesReport}>
            üìä Generate Sales Report
          </button>
          <button className="btn btn-outline" onClick={() => navigate('/admin/orders')}>
            üóÇÔ∏è Manage Orders
          </button>
          <button className="btn btn-outline" onClick={() => { setShowUsers(true); fetchUsers(); }}>
            üë• Manage Users
          </button>
          <button className="btn btn-success" onClick={() => openModal()}>
            ‚ûï Add New Book
          </button>
        </div>
      </div>
      {/* Users Table Modal */}
      {showUsers && (
        <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(0,0,0,0.5)', zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div className="card" style={{ width: '90%', maxWidth: '900px', maxHeight: '90vh', overflow: 'auto', position: 'relative' }}>
            <h3>üë• Users Management</h3>
            <button className="btn btn-secondary" style={{ position: 'absolute', top: 10, right: 10 }} onClick={() => setShowUsers(false)}>Close</button>
            {usersLoading ? (
              <div className="loading">Loading users...</div>
            ) : usersError ? (
              <div className="alert alert-error">{usersError}</div>
            ) : (
              <div className="table-container" style={{ overflowX: 'auto', marginTop: '1rem' }}>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                  <thead>
                    <tr style={{ backgroundColor: '#f5f5f5' }}>
                      <th style={{ padding: '1rem', textAlign: 'left', borderBottom: '2px solid #ddd' }}>Name</th>
                      <th style={{ padding: '1rem', textAlign: 'left', borderBottom: '2px solid #ddd' }}>Email</th>
                      <th style={{ padding: '1rem', textAlign: 'left', borderBottom: '2px solid #ddd' }}>Phone</th>
                      <th style={{ padding: '1rem', textAlign: 'left', borderBottom: '2px solid #ddd' }}>Address</th>
                      <th style={{ padding: '1rem', textAlign: 'center', borderBottom: '2px solid #ddd' }}>Active</th>
                      <th style={{ padding: '1rem', textAlign: 'center', borderBottom: '2px solid #ddd' }}>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {users.map(user => (
                      <tr key={user.userId} style={{ borderBottom: '1px solid #eee' }}>
                        <td style={{ padding: '1rem' }}>{user.userFirstName} {user.userLastName}</td>
                        <td style={{ padding: '1rem' }}>{user.userEmail}</td>
                        <td style={{ padding: '1rem' }}>{user.userPhoneNumber}</td>
                        <td style={{ padding: '1rem' }}>{user.contact?.address || 'N/A'}</td>
                        <td style={{ padding: '1rem', textAlign: 'center' }}>
                          {user.active ? (
                            <span style={{ color: 'green', fontWeight: 'bold' }}>Active</span>
                          ) : (
                            <span style={{ color: 'red', fontWeight: 'bold' }}>Inactive</span>
                          )}
                        </td>
                        <td style={{ padding: '1rem', textAlign: 'center' }}>
                          <div style={{ display: 'flex', gap: '0.5rem', justifyContent: 'center' }}>
                            <button
                              className="btn btn-secondary"
                              style={{ fontSize: '0.8rem', padding: '0.3rem 0.6rem' }}
                              onClick={() => alert('Edit user not implemented in dashboard. Use Users page for full edit.')}
                            >
                              Edit
                            </button>
                            {user.active ? (
                              <button
                                className="btn"
                                style={{ backgroundColor: '#ffc107', color: '#333', fontSize: '0.8rem', padding: '0.3rem 0.6rem' }}
                                onClick={() => deactivateUser(user.userId)}
                              >
                                Deactivate
                              </button>
                            ) : (
                              <button
                                className="btn"
                                style={{ backgroundColor: '#28a745', color: '#fff', fontSize: '0.8rem', padding: '0.3rem 0.6rem' }}
                                onClick={() => reactivateUser(user.userId)}
                              >
                                Reactivate
                              </button>
                            )}
                            <button
                              className="btn"
                              style={{ backgroundColor: '#dc3545', color: '#fff', fontSize: '0.8rem', padding: '0.3rem 0.6rem' }}
                              onClick={() => deleteUser(user.userId)}
                            >
                              Delete
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      )}
      {/* ...existing dashboard content... */}
    </div>
  );
};

export default AdminDashboard;
