import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';

const Register = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [formData, setFormData] = useState({
    userFirstName: '',
    userLastName: '',
    userEmail: '',
    userPassword: '',
    confirmPassword: '',
    userPhoneNumber: '',
    studentNumber: '',
    address: ''
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.userFirstName.trim()) {
      newErrors.userFirstName = 'First name is required';
    }

    if (!formData.userLastName.trim()) {
      newErrors.userLastName = 'Last name is required';
    }

    if (!formData.userEmail.trim()) {
      newErrors.userEmail = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.userEmail)) {
      newErrors.userEmail = 'Email is invalid';
    } else if (!formData.userEmail.includes('.ac.za')) {
      newErrors.userEmail = 'Please use your university email address (.ac.za)';
    }

    if (!formData.userPassword) {
      newErrors.userPassword = 'Password is required';
    } else if (formData.userPassword.length < 6) {
      newErrors.userPassword = 'Password must be at least 6 characters';
    }

    if (formData.userPassword !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    if (!formData.userPhoneNumber.trim()) {
      newErrors.userPhoneNumber = 'Phone number is required';
    }

    if (!formData.studentNumber.trim()) {
      newErrors.studentNumber = 'Student number is required';
    } else if (!/^\d{6,10}$/.test(formData.studentNumber)) {
      newErrors.studentNumber = 'Student number must be 6-10 digits';
    }

    if (!formData.address.trim()) {
      newErrors.address = 'Address is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    setIsLoading(true);
    setMessage('');

    try {
      const registrationData = {
        userFirstName: formData.userFirstName,
        userLastName: formData.userLastName,
        userEmail: formData.userEmail,
        userPassword: formData.userPassword,
        userPhoneNumber: formData.userPhoneNumber,
        studentNumber: formData.studentNumber,
        contact: {
          email: formData.userEmail,
          phoneNumber: formData.userPhoneNumber,
          address: formData.address,
          password: formData.userPassword
        }
      };

      let response;
      try {
        response = await axios.post('/api/users/create', registrationData);
      } catch (apiError) {
        // Demo mode fallback
        console.log('API registration failed, using demo mode');
      }
      
      // Create user object for login
      const userData = {
        userId: Date.now().toString(),
        userFirstName: formData.userFirstName,
        userLastName: formData.userLastName,
        userEmail: formData.userEmail,
        userPhoneNumber: formData.userPhoneNumber,
        studentNumber: formData.studentNumber,
        userType: 'customer'
      };

      // Auto-login after successful registration
      login(userData);
      setMessage('Registration successful! Welcome to Snuggle Read!');
      
      setTimeout(() => {
        navigate('/books');
      }, 2000);

    } catch (error) {
      console.error('Registration error:', error);
      setMessage('Registration failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="card" style={{ maxWidth: '600px', margin: '2rem auto' }}>
        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
          <h2>üéì Join Snuggle Read</h2>
          <p>Create your student account and start your reading journey</p>
        </div>
        
        {message && (
          <div className={`alert ${message.includes('successful') ? 'alert-success' : 'alert-error'}`}>
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
            <div className="form-group">
              <label htmlFor="userFirstName">First Name:</label>
              <input
                type="text"
                id="userFirstName"
                name="userFirstName"
                value={formData.userFirstName}
                onChange={handleChange}
                placeholder="Enter your first name"
              />
              {errors.userFirstName && <div className="error">{errors.userFirstName}</div>}
            </div>

            <div className="form-group">
              <label htmlFor="userLastName">Last Name:</label>
              <input
                type="text"
                id="userLastName"
                name="userLastName"
                value={formData.userLastName}
                onChange={handleChange}
                placeholder="Enter your last name"
              />
              {errors.userLastName && <div className="error">{errors.userLastName}</div>}
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="userEmail">University Email:</label>
            <input
              type="email"
              id="userEmail"
              name="userEmail"
              value={formData.userEmail}
              onChange={handleChange}
              placeholder="student@university.ac.za"
            />
            {errors.userEmail && <div className="error">{errors.userEmail}</div>}
          </div>

          <div className="form-group">
            <label htmlFor="studentNumber">Student Number:</label>
            <input
              type="text"
              id="studentNumber"
              name="studentNumber"
              value={formData.studentNumber}
              onChange={handleChange}
              placeholder="Your 6-10 digit student number"
            />
            {errors.studentNumber && <div className="error">{errors.studentNumber}</div>}
          </div>

          <div className="form-group">
            <label htmlFor="userPhoneNumber">Phone Number:</label>
            <input
              type="tel"
              id="userPhoneNumber"
              name="userPhoneNumber"
              value={formData.userPhoneNumber}
              onChange={handleChange}
              placeholder="+27 XX XXX XXXX"
            />
            {errors.userPhoneNumber && <div className="error">{errors.userPhoneNumber}</div>}
          </div>

          <div className="form-group">
            <label htmlFor="address">Residential Address:</label>
            <textarea
              id="address"
              name="address"
              value={formData.address}
              onChange={handleChange}
              placeholder="Your complete residential address for delivery"
              rows="3"
            />
            {errors.address && <div className="error">{errors.address}</div>}
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
            <div className="form-group">
              <label htmlFor="userPassword">Password:</label>
              <input
                type="password"
                id="userPassword"
                name="userPassword"
                value={formData.userPassword}
                onChange={handleChange}
                placeholder="Create a secure password"
              />
              {errors.userPassword && <div className="error">{errors.userPassword}</div>}
            </div>

            <div className="form-group">
              <label htmlFor="confirmPassword">Confirm Password:</label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
                placeholder="Confirm your password"
              />
              {errors.confirmPassword && <div className="error">{errors.confirmPassword}</div>}
            </div>
          </div>

          <button 
            type="submit" 
            className="btn"
            disabled={isLoading}
            style={{ width: '100%', marginBottom: '1rem' }}
          >
            {isLoading ? 'Creating Account...' : 'üéì Create Student Account'}
          </button>
        </form>

        <div style={{ textAlign: 'center', marginTop: '1.5rem' }}>
          <p>Already have an account? 
            <Link to="/login" style={{ marginLeft: '0.5rem', color: '#2a5298', textDecoration: 'none', fontWeight: 'bold' }}>
              Sign In Here
            </Link>
          </p>
        </div>

        {/* Benefits */}
        <div className="card" style={{ 
          marginTop: '2rem', 
          background: 'linear-gradient(135deg, #e8f5e8, #f0f8ff)',
          border: '2px solid #27ae60'
        }}>
          <h4 style={{ marginBottom: '1rem', color: '#27ae60' }}>üéÅ Student Benefits</h4>
          <div style={{ fontSize: '0.9rem', lineHeight: '1.6' }}>
            ‚úÖ 5% automatic student discount on all purchases<br />
            üìö Access to exclusive academic titles<br />
            üöö Free shipping on orders over R500<br />
            üì± Digital access to select e-books<br />
            üîÑ Easy returns and exchanges<br />
            üí¨ Priority customer support
          </div>
        </div>

        <div style={{ textAlign: 'center', marginTop: '1.5rem', fontSize: '0.8rem', color: '#7f8c8d' }}>
          <p>By creating an account, you agree to our Terms of Service and Privacy Policy.</p>
          <p>üîí Your information is secure and will never be shared with third parties.</p>
        </div>
      </div>
    </div>
  );
};

export default Register;
